import React from 'react';
import {useTranslation} from 'react-i18next';

const hasActivatedMenu = (menu, currentLanguage, defaultLanguage, windowHref) => {
  const translation = menu.translations?.[currentLanguage];
  if (!translation) {
    return false;
  }
  const url = translation.url;
  if (linkIsCurrentPage(url, currentLanguage, defaultLanguage, windowHref)) {
    return true;
  } else {
    // Check deeper levels
    for (const menuItem of menu.children || []) {
      if (hasActivatedMenu(menuItem, currentLanguage, defaultLanguage, windowHref)) {
        return true;
      }
    }

    // If no activated menu found
    return false;
  }
};

function LeftSidebar() {
  const {settings} = SitePublicSettingsState();
  const {menus} = settings;
  const {i18n} = useTranslation();
  const currentLanguage = i18n.language;
  const defaultLanguage = settings?.default_language?.iso_code;
  const windowHref = window.location.href;

  const processedMenus = menus
    ? React.useMemo(() => {
        for (const menu of menus.main) {
          if (hasActivatedMenu(menu, currentLanguage, defaultLanguage, windowHref)) {
            return menu.children || [];
          }
        }
        return [];
      }, [menus.main, currentLanguage, defaultLanguage, windowHref])
    : [];

  const widthClass = React.useMemo(() => {
    if (processedMenus.length && processedMenus.length > 0) {
      return 'w-[150px] lg:w-[200px] lg:w-[220px] xl:w-[240px] 2xl:w-[260px]';
    }
    // produce spacer to let main content be in center
    return 'invisible w-0 lg:w-[200px] lg:w-[220px] xl:w-[240px] 2xl:w-[260px]';

  }, [processedMenus]);

  return (
    <nav className={`left-sidebar text-sm lg:text-base hidden md:block ${widthClass}`}>
      {processedMenus.map((menu, index) => (
        <LeftMenuItem key={index} menu={menu} index={index} />
      ))}
    </nav>
  );
}
